<template>
  <div class="messages">
    <v-card elevation="5" class="main chat-box">
      <v-container>
        <div class="first-box  fs-small grey--text" v-if="!selectedUser.id">
          <div>Choose a chat...</div>
        </div>
        <div
          @keyup.escape="selectUser({})"
          style="outline: none !important"
          tabindex="0"
          v-else
        >
          <div class="chat-header d-flex flex-row">
            <div class="d-sm-none d-block pr-2 py-6">
              <v-icon @click="selectUser({})">mdi-arrow-left</v-icon>
            </div>
            <div class="image-box ml-2">
              <v-badge
                bottom
                :color="selectedUser.lastSeen == 'online' ? 'green accent-4' : ''"
                dot
                offset-x="10"
                offset-y="10"
              >
                <v-avatar size="55">
                  <v-img
                    :src="
                      selectedUser.avatar
                        ? selectedUser.avatar
                        : 'data:image/png;base64,'
                    "
                  ></v-img>
                </v-avatar>
              </v-badge>
            </div>
            <div class="px-1 py-3">
              <h4>{{ selectedUser.name }}</h4>
              <h6 class="fs-xsmall grey--text">{{ selectedUser.lastSeen == 'online' ? selectedUser.lastSeen : `last seen at ${selectedUser.lastSeen}` }}</h6>
            </div>
            <v-spacer></v-spacer>
          </div>
          <div>
            <div id="chat-body" class="chat-body">
              <div v-if="selectedChat.messages.length">
                <div v-for="(msg, index) in selectedChat.messages" :key="index">
                  <v-row>
                    <v-col :class="msg.self ? 'text-right' : 'text-left'">
                      <div
                        :style="msg.self ? 'float: right' : 'float:left'"
                        class="message"
                        :class="
                          msg.self
                            ? 'user1 white--text'
                            : 'user2 commonText--text'
                        "
                        elevation="3"
                        max-width="350"
                      >
                        <div class="d-flex flex-row justify-space-between px-1">
                          <span class="text-right fs-xsmall">
                            {{ msg.message }}
                          </span>
                        </div>
                        <div class="text-right pt-1">
                          <span class="fs-xxsmall">{{ msg.time }}</span>
                          <span v-if="msg.self">
                            <v-icon small v-if="msg.read">mdi-check-all</v-icon>
                            <v-icon small v-else>mdi-check</v-icon>
                          </span>
                        </div>
                      </div>
                    </v-col>
                  </v-row>
                </div>
              </div>
              <div class="first-box fs-small grey--text" v-else>
                <div>say something to {{ selectedUser.name }}!</div>
              </div>
            </div>
          </div>

          <div>
            <div class="chat-footer">
              <v-text-field
                v-model="newMessage"
                outlined
                placeholder="Write a message..."
                @keyup.enter="newMessage && send()"
              >
                <template v-slot:append>
                  <v-btn color="primary" text :disabled="!newMessage" @click="send">
                    <v-icon>mdi-send</v-icon>
                  </v-btn>
                </template>
              </v-text-field>
            </div>
          </div>
        </div>
      </v-container>
    </v-card>
  </div>
</template>
<script>
import { mapMutations, mapGetters } from 'vuex'
export default {
  data() {
    return {
      newMessage: '',
    }
  },
  methods: {
    ...mapMutations(['sendMessage', 'selectUser']),
    send() {
      this.sendMessage(this.newMessage)
      this.newMessage = ''
      setTimeout(() => {
        this.scrollToEnd()
      }, 400)
    },
    scrollToEnd() {
      // this function scroll the page to the bottom
      var container = document.querySelector('#chat-body')
      var scrollHeight = container.scrollHeight
      console.log('scrolling...', scrollHeight)
      container.scrollTop = scrollHeight
    },
  },
  watch: {
    userId(val) {
      if (val) {
        setTimeout(() => {
          this.scrollToEnd()
        }, 400)
      }
    },
  },
  computed: {
    ...mapGetters(['selectedChat', 'selectedUser', 'userId']),
  },
}
</script>
